-- ----------------------------
--  Table structure for `lxlj_student_feedback`
-- ----------------------------
DROP TABLE IF EXISTS `lxlj_student_feedback`;
CREATE TABLE `lxlj_student_feedback` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `order_id` int(11) NOT NULL,
  `is_right_time` tinyint(1) NOT NULL COMMENT '本次补课是否为规定时间',
  `who_change_time` tinyint(1) DEFAULT NULL COMMENT '2家长，1老师, 0未修改',
  `is_teacher_late` tinyint(1) NOT NULL COMMENT '本次补课老师是否迟到.0未迟到。 1:10分钟以内,-5块；2:10－30分钟,-20块；3:30－60分钟,-50块；4:60分钟以上,-50块',
  `is_over_time` tinyint(1) NOT NULL COMMENT '.本次补课是否超时：是，是的话继续选择： A：0－20分钟；B：20－50分钟；下次多收费30 C：50分钟以上下次多收费50',
  `is_use_phone` tinyint(1) NOT NULL COMMENT '本次上课老师是否使用手机：',
  `is_like_teach_style` tinyint(1) NOT NULL COMMENT '是否喜欢老师本次课的上课风格',
  `learn_percent` tinyint(1) NOT NULL COMMENT '本堂课老师所讲知识点掌握程度： 1.90％以上.. 2.80－90％ ...3. 50－80％...4. 50％以下；填写原因',
  `learn_percent_reason` text COLLATE utf8_unicode_ci,
  `learn_time` tinyint(1) NOT NULL COMMENT '90％ 70－90％ 50％－70％ 50％以下',
  `interesting_level` tinyint(1) NOT NULL COMMENT '非常有趣，让我整堂课精力高度集中 有趣，让我大部分时间都能跟上老师的思维 无聊，但是还是能听懂老师讲的知识点 无聊，主要是因为知识点太难，基础太差，好多都没听懂',
  `grade` int(4) NOT NULL COMMENT '最近所补课科目考试分数',
  `self_grade` int(4) NOT NULL COMMENT '对自己本堂课打分（满分100）',
  `discontent` text COLLATE utf8_unicode_ci COMMENT '对自己本堂课不满意的地方',
  `time` int(11) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=18 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

-- ----------------------------
--  Table structure for `lxlj_teacher`
-- ----------------------------
DROP TABLE IF EXISTS `lxlj_teacher`;
CREATE TABLE `lxlj_teacher` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `openid` varchar(200) NOT NULL,
  `phone` varchar(20) DEFAULT NULL,
  `name` varchar(50) NOT NULL,
  `sex` varchar(10) NOT NULL,
  `age` int(5) NOT NULL,
  `shenfen` varchar(50) NOT NULL,
  `university` varchar(50) DEFAULT NULL COMMENT '家教所在大学',
  `grade` varchar(50) DEFAULT NULL COMMENT '家教年级',
  `major` varchar(50) DEFAULT NULL COMMENT '家教所学专业',
  `teach_grades` varchar(255) DEFAULT NULL COMMENT '家教所能教授的年级',
  `teach_subjects` varchar(255) DEFAULT NULL COMMENT '家教所能教授的学科',
  `gaokao_grade` int(5) DEFAULT NULL COMMENT '家教高考分数',
  `organization` varchar(50) DEFAULT NULL COMMENT '机构名称',
  `graduate_school` varchar(30) DEFAULT NULL COMMENT '毕业院校',
  `job_school` varchar(30) DEFAULT NULL COMMENT '在职学校',
  `school_type` varchar(10) DEFAULT NULL COMMENT '学校类型',
  `personal_title` varchar(50) DEFAULT NULL COMMENT '个性标题',
  `teach_way` varchar(200) DEFAULT NULL COMMENT '授课方式',
  `cost` int(11) DEFAULT NULL COMMENT '课时费',
  `free_time` varchar(200) DEFAULT NULL COMMENT '空闲时间',
  `free_time_description` text,
  `teach_location` varchar(200) DEFAULT NULL COMMENT '可授课区域',
  `teach_info_efficacy` varchar(50) DEFAULT NULL COMMENT '家教信息有效时间',
  `good_teach_type` varchar(50) DEFAULT NULL COMMENT '擅长类型学生',
  `good_teach_type_reason` varchar(255) DEFAULT NULL COMMENT '擅长类型学生理由',
  `avatar` varchar(200) DEFAULT NULL COMMENT '头像',
  `id_card` varchar(200) DEFAULT NULL COMMENT '身份证',
  `teacher_card` varchar(200) DEFAULT NULL COMMENT '教师证/学生证',
  `other_card` varchar(200) DEFAULT NULL COMMENT '其他证件',
  `id_card_type` tinyint(1) NOT NULL DEFAULT '0' COMMENT '身份证是否验证',
  `teacher_card_type` tinyint(1) NOT NULL DEFAULT '0' COMMENT '教师证/学生证是否验证',
  `other_card_type` tinyint(1) NOT NULL DEFAULT '0' COMMENT '其他证件是否验证',
  `sign_time` int(11) NOT NULL,
  `status` tinyint(1) NOT NULL DEFAULT '2' COMMENT '1为可用，0为不可用',
  `experience` text,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=225 DEFAULT CHARSET=utf8;

